@startuml
Alice -> Bob: Request
Bob --> Alice: Response
@enduml

@startuml
start
:User submits form;
if (Valid?) then (yes)
  :Process request;
else (no)
  :Show error;
endif
stop
@enduml

@startuml
class User {
  +id: int
  +name: string
}
class Order {
  +orderId: int
  +total: float
}
User "1" -- "*" Order
@enduml

@startuml
actor User
rectangle System {
  User -- (Login)
  User -- (Place Order)
}
@enduml

@startuml
node "Web Server" {
  component "Web App"
}
node "DB Server" {
  database "PostgreSQL"
}
"Web App" --> "PostgreSQL"
@enduml

